from selenium import webdriver
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.common.action_chains import ActionChains
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.webdriver.common.by import By
import time

PATH = "C:\Program Files (x86)\chromedriver.exe"
driver = webdriver.Chrome(PATH)

driver.get("https://orteil.dashnet.org/cookieclicker/")

driver.implicitly_wait(10)




try:
    time.sleep(5)
    options = WebDriverWait(driver, 10).until(
        EC.presence_of_element_located((By.CSS_SELECTOR, "#prefsButton"))
    )
    options.click()

    time.sleep(1)

    importsave = WebDriverWait(driver, 10).until(
        EC.presence_of_element_located((By.XPATH, "//a[normalize-space()='Import save']"))
    )
    importsave.click()

    time.sleep(0.5)
    
    savetext = WebDriverWait(driver, 10).until(
        EC.presence_of_element_located((By.ID, "textareaPrompt"))
    )
    savetext.send_keys("%3D%3D%21END%21")
    savetext.send_keys(Keys.RETURN)
    time.sleep(1)
    
    closeMenu = WebDriverWait(driver, 10).until(
        EC.presence_of_element_located((By.XPATH, "//div[@class='close menuClose']"))
    )
    closeMenu.click()

    time.sleep(0.5)
    
    cookie = driver.find_element(By.ID, "bigCookie")

    time.sleep(10)

    actions = ActionChains(driver)


    for i in range(500000):
        actions.click(cookie)
        actions.perform()



except:
    driver.quit()



